    this.showIcon = function (textValue) {
        let iconContainer = this.model.icon_visible && this.model.workflow_icon ? this.model.workflow_icon : "";
        let itemLabel = "";
        let hover_html = `<span class = 'hover_text'>${textValue.hover_text} </span>`
        if (!textValue.label) {
            itemLabel = this.model.button_label
        }
        else
            itemLabel = textValue.label;
        let iconTemplate = "";
        if (this.model.side_to_display == "right") {
            iconTemplate = `<span data-localize-text='${itemLabel}'>${translateText(itemLabel)}</span><span class='right_side' style='margin-left:8px;'><i class='${iconContainer}'></i></span>${hover_html}`;
